class SearchAndFilter {
  searchAndFilter(courses, search, subject, minimumCredits, maximumCredits) {
    var retCourses = []
    console.log(subject)
    console.log(minimumCredits)
    console.log(maximumCredits)
    console.log(search)
    
    for (const index in courses){
      var cur = courses[index];

      // filter subject
      if (subject && subject !== "All" && cur.subject !== subject){
        continue;
      }

      // filter credit
      if (minimumCredits && cur.credits < minimumCredits){
        continue;
      }
      if (maximumCredits && cur.credits > maximumCredits){
        continue;
      }

      // filter keywords
      if (search && !cur.keywords.includes(search)){
        continue;
      }

      retCourses.push(cur);
    } 
    
    return retCourses;
  }
}

export default SearchAndFilter;
