import React from 'react';
import './App.css';
import Course from './Course';

class CourseArea extends React.Component {
  getCourses() {
    let courses = [];

    for(const course of Object.values(this.props.data)) {
      courses.push (
        <Course key={course.name} data={course} addCourse={this.props.addCourse} cartMode={this.cartMode}/>
      )
    }

    return courses;
  }

  render() {
    if (!this.props.cartMode){

      return (

        <div style={{margin: '5px'}}>
          {this.getCourses()}
        </div>
      )
    }else{
      // cartmode
      return (
        <div>
          {this.getCourses()}
        </div>
      )
    }

  }
}

export default CourseArea;
