import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      parsedReq: this.parseReq(this.props.data.requisites),
  }; }

  // function to parse the prerequisties
  parseReq(data) {
    if (!data.length){
      // no req
      return "None";
    }
    let arrToJoin = [];
    for (const cur in data){
      arrToJoin.push(data[cur].join(" OR "));
    }

    return arrToJoin.join(" AND ");
  }

  // show sections of the course
  showSection(id){
    console.log(id)
    console.log(document.getElementById(id).innerHTML)
    document.getElementById(id).style.diaplay = "block";
    console.log(document.getElementById(id).style)
  }

  // hide sections of the course
  hideSection(id){
    document.getElementById(id).style.diaplay = "none";
  }


  render() {
    // console.log(this.props.data);
    return ( 
      <div > 
        <React.Fragment>
          <h2>
            {this.props.data.number}: {this.props.data.name}
          </h2>
          <p>
            Credits: {this.props.data.credits}
          </p>
          <p>
            requisites: {this.state.parsedReq}
          </p>
          <p>
            keywords: {this.props.data.keywords.join(", ")}
          </p>
          <p>
            description: {this.props.data.description}
          </p>

          {/* show section, add course */}
          <button  onClick={() => this.showSection(this.props.data.number)}>Show Sections</button>
          <div id={this.props.data.number}>
            {this.props.data.number}
            <Section data={this.props.data.sections}/>
            <button  onClick={() => this.hideSection(this.props.data.number)}>Hide Sections</button>
          </div>
          <button>Add Course</button>
          
        </React.Fragment>            
        
                  
  

        {/* { this.props.data.number };  */}
      </div>
    )
  }
}

export default Course;
