import React from 'react';
import './App.css';
import Course from './Course';

class CourseArea extends React.Component {
  getCourses() {
    let courses = [];

    for(const course of Object.values(this.props.data)) {
      if(!this.props.cartMode){
        // search
        courses.push (
          <Course key={course.name} data={course} addCourse={this.props.addCourse} subSectionCallbackPass2={this.props.addSubSection} sectionCallbackPass={this.props.addSection} cartMode={this.props.cartMode}/>
        )
      }else{
        // cart
        courses.push (
          <Course key={course.name} data={course} addCourse={this.props.remCourse} subSectionCallbackPass2={this.props.remSubSection} sectionCallbackPass={this.props.remSection} cartMode={this.props.cartMode}/>
        )
      }
    }

    return courses;
  }

  render() {
    console.log(this.props.data)
    if (!this.props.cartMode){

      return (

        <div style={{margin: '5px'}}>
          {this.getCourses()}
        </div>
      )
    }else{
      // cartmode
      return (
        <div>
          {this.getCourses()}
        </div>
      )
    }

  }
}

export default CourseArea;
