import React from 'react'
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      parsedTimes: this.parseTimes(this.props.data.times),
  }; }

  parseTimes(data){
    console.log(data);
    if (!data.length){
      // no time
      return "None";
    }
    let arrToJoin = [];
    for (const cur in data){
      console.log(data[cur]);
      // arrToJoin.push(data[cur].join(" OR "));
    }

    return "1";
    // return arrToJoin.join(" AND ");
  }

	render() {
		console.log(this.props.data);

		return (
			<React.Fragment>
        <tr>
          <td> {this.props.data.number} </td>
          <td> {this.props.data.instructor} </td>
          <td> {this.props.data.location} </td>
          <td> {this.props.state.parsedTimes} </td>
          <td> <button>Add Section</button> </td>
        </tr>

				{/* subsections */}
				{this.props.data.subsections.map(subsection => (
					<Subsection data={subsection}/>
          ))}

			</React.Fragment>
		)
	}
}

export default Section;
