import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      parsedReq: this.parseReq(this.props.data.requisites),
      showSection: false,
  }; }

  // function to parse the prerequisties
  parseReq(data) {
    if (!data.length){
      // no req
      return "None";
    }
    let arrToJoin = [];
    for (const cur in data){
      arrToJoin.push(data[cur].join(" OR "));
    }

    return arrToJoin.join(" AND ");
  }

  // show sections of the course
  show(){
    this.setState({ showSection: true });
  }

  // hide sections of the course
  hide(){
    this.setState({ showSection: false});
  }


  render() {
    // console.log(this.props.data);
    return ( 
        <React.Fragment>
          <h2>
            {this.props.data.number}: {this.props.data.name}
          </h2>
          <p>
            Credits: {this.props.data.credits}
          </p>
          <p>
            requisites: {this.state.parsedReq}
          </p>
          <p>
            keywords: {this.props.data.keywords.join(", ")}
          </p>
          <p>
            description: {this.props.data.description}
          </p>

          {/* show section, add course */}
          <button  onClick={() => this.show()}>Show Sections</button>
          <button>Add Course</button>

          <div id={this.props.data.number} style={{display: this.state.showSection? "block": "none"}}>
            <tr>
              <th>
                Number
              </th>
              <th>
                Instructor
              </th>
              <th>
                Location
              </th>
              <th>
                Option
              </th>
            </tr>
            {this.props.data.sections.map(section => (
              <Section data={section}/>
            ))}
            
            <button onClick={() => this.hide()}>Hide Sections</button>
          </div>
          
        </React.Fragment>            

    )
  }
}

export default Course;
