import React from 'react'
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      parsedTime: this.parseTimes(this.props.data.time),
  }; }

  parseTimes(data){
    var returnStr = "";
    for (var key in data) {
      // check if the property/key is defined in the object itself, not in parent
      if (data.hasOwnProperty(key)) {           
          returnStr += key + ": " + data[key] + "\n";
      }
    }

    return returnStr;
    // return arrToJoin.join(" AND ");
  }

	render() {
    console.log("section", this.props.cartMode)
    console.log(this.props.data)
		return (
			<React.Fragment>
        <tr style={{backgroundColor: 'lightGrey'}}>
          <td> {this.props.data.number} </td>
          <td> {this.props.data.instructor} </td>
          <td> {this.props.data.location} </td>
          <td> <pre>{this.state.parsedTime} </pre></td>
          <td> <button>{this.props.cartMode? "Remove Section" : "Add Section"}</button> </td>
        </tr>

				{/* subsections */}
				{this.props.data.subsections.map(curSubsection => (
					<Subsection key={this.props.id + curSubsection.number} data={curSubsection} subSectionCallback={this.props.subSectionCallbackPass1} cartMode={this.props.cartMode} id={this.props.id}/>
          ))}

			</React.Fragment>
		)
	}
}

export default Section;
