import React from 'react';
import './App.css';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Sidebar from './Sidebar';
import CourseArea from './CourseArea';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      subjects: [],
      addedCourses: [],
    };
  }

  componentDidMount() {
    fetch('http://mysqlcs639.cs.wisc.edu:53706/api/react/classes').then(
      res => res.json()
    ).then(data => this.setState({allCourses: data, filteredCourses: data, subjects: this.getSubjects(data)}));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");

    for(const course of Object.values(data)) {
      if(subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }

    return subjects;
  }

  setCourses(courses) {
    this.setState({filteredCourses: courses});
  }

  addCourse(course) {
    // check whether the course was added before
    var joined = this.state.addedCourses;
    var added = 0;
    for (const index in joined){
      //  course added before
      if (joined[index].number === course.number){
        joined[index] = course;
        added = 1;
      }
    }

    if (!added){
      // course not added before
      joined = joined.concat(course);
    }

    this.setState({ addedCourses: joined })
  }

  addSubSection(subSection){
    console.log(subSection);
    // split to get course, section, subsetion number
    var splited = subSection.split(";");
    var courseNum = splited[0];
    var sectionNum = splited[1];
    var subSecNum = splited[2];

    // get the data for current course
    var curCourse;
    var curSec;
    var curSubSec;
    var i, j, k;
    for (i = 0; i< this.state.allCourses.length; i ++ ){
      if (this.state.allCourses[i].number === courseNum){
        curCourse = JSON.parse(JSON.stringify(this.state.allCourses[i]));
        for (j = 0; j < curCourse.sections.length; j++){
          if (curCourse.sections[j].number === sectionNum){
            curSec = JSON.parse(JSON.stringify(curCourse.sections[j]));
            for (k = 0; k < curSec.subsections.length; k ++){
              console.log(curSec.subsections);
              if (curSec.subsections[i].number === subSecNum){
                curSubSec = curSec.subsections[i]
                break;
              }
            }
            break;
          }
        }
        break;
      }
    }
    console.log(curCourse);
    console.log(curSec);
    console.log(curSubSec);


    // traverse through the addedCourse to see whether current course exist
    var joined = this.state.addedCourses;
    var added = 0;
    for (i = 0; i< joined.length; i ++ ){
      //  course added before
      if (joined[i].number === courseNum){
        // cur course exist, check section
        for (j = 0; j< joined[i].sections.length; j ++){
          if(joined[i].sections[j].number === sectionNum){
            // cur section exist, check subsection
            for (k = 0; k< joined[i].sections[j].length; k ++){
              if (joined[i].sections[j].subsections[k].number === subSecNum){
                added = 1;
                break;
              }
            }
            if (!added){
              // add the subsection
              joined[i].sections[j].subsections[subSecNum] = curSubSec;
              added = 1;
            }
            break;
          }
        }
        // cur course exist, section and subsection not exist
        if (!added){
          joined[i].sections[sectionNum] = curSec;
          joined[i].sections[sectionNum].subsections = [];
          joined[i].sections[sectionNum].subsections[subSecNum] = curSubSec;
          added = 1;
        }
        break;
      }
    }
    if (!added){
      // not added before
      curCourse.sections = [];
      curCourse.sections[sectionNum] = curSec;
      curCourse.sections[sectionNum].subsections = [];
      curCourse.sections[sectionNum].subsections[subSecNum] = curSubSec;

      joined = joined.concat(curCourse);
    }
    console.log(curCourse);
    console.log(joined);
    console.log(this.state.allCourses)

    this.setState({ addedCourses: joined });
  }

  render() {
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />



        <Tabs defaultActiveKey="search" style={{position: 'fixed', zIndex: 1, width: '100%', backgroundColor: 'white'}}>
          <Tab eventKey="search" title="Search" style={{paddingTop: '5vh'}}>
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects}/>
            <div style={{marginLeft: '20vw'}}>
              <CourseArea addCourse={this.addCourse.bind(this)} data={this.state.filteredCourses} allData={this.state.allCourses} addSubSection={this.addSubSection.bind(this)} cartMode={false}/>
          </div>
          </Tab>

          <Tab eventKey="cart" title="Cart" style={{paddingTop: '5vh'}}>
            <div style={{marginLeft: '5vw'}}>
              <CourseArea data={this.state.addedCourses} allData={this.state.allCourses} cartMode={true}/>
            </div>
          </Tab>
        </Tabs> 
      </>
    )
  }
}

export default App;
