import React from 'react';
import './App.css';
import RecomCourse from './RecomCourse';

class RecomCourseArea extends React.Component {
  getCourses() {
    let completed = Object.values(this.props.compleCourses)[0];
    let courses = [];
    let interests = [];
    let added = [];
    let wishReq = {};
    console.log(this.props.wishCourses)
    // add wish courses and their dependencies first
    var i;
    for (const course of this.props.wishCourses) {
      console.log(course);
      // add to wishlist because there are prereq not fulfilled
      var cur_course = course.split(";")[0];
      var cur_req = course.split(";")[1];
      // add the wished course
      courses.push(
        <RecomCourse key={"recom " + cur_course} data={cur_course} interests={""} waitList={"added to the waitlist"}/>
      );
      added.push(
        cur_course.split(":")[0]
      );
      // add the prereqs
      var cur_req_list = cur_req.replace(") AND (", ";").replace(" OR ",";").replace("(","").replace(")", "").split(";")
      for (i = 0; i < cur_req_list; i ++){
        if (cur_req_list[i] in wishReq){
          wishReq[cur_req_list[i]].push(cur_course)
        }else{
          wishReq[cur_req_list[i]] = [cur_course]
        }
      }
    }

    // add wish list prereq
    for (const course of Object.values(this.props.courses)) {
      if (cur_req_list.includes(course.number)){
        courses.push(
          <RecomCourse key={"recom " + cur_course} data={cur_course} interests={""} waitList={"a requisites for the course(s) in your wishlist:" + wishReq[cur_req_list[i]]}/>
        );
        added.push(
          course.number
        );
      }
    }

    // generate the list of interests
    for (const course of Object.values(this.props.courses)) {
      // generate the list of interests
      if (this.props.interestedCourses.indexOf(course.number) !== -1)
        for (const keyword of course.keywords)
          if (interests.indexOf(keyword) === -1)
            interests.push(keyword);
    }

    // get courses based on interests
    for (const course of Object.values(this.props.courses)) {
      // courses with interested keyword but not completed
      if (completed && completed.includes(course.number)) {
        continue;
      }
      // skip added courses from wish list
      if (added && added.includes(course.number)) {
        continue;
      }

      var cur_interest = "";
      for (const keyword of course.keywords)
          if (interests.indexOf(keyword) !== -1)
            cur_interest += keyword + ", "

      // if some keyword is interested
      if (cur_interest !== ""){
        courses.push(
          <RecomCourse key={"recom " + course.name} data={course} interests={cur_interest.substring(0, cur_interest.length - 2)}/>
        )
      }

    }

    return courses;
  }

  render() {
    return (
      <React.Fragment>
        <h3 style={{display: this.props.interestedCourses.length ? "none":"block"}}> Give high ratings to the completed courses that you are interested in to find recommendations!</h3>

        <div class="h-100 mt-2 ml-1">
          {this.getCourses()}
        </div>
      </React.Fragment>
    );
  }
}

export default RecomCourseArea;
