import React from 'react';
import './App.css';
import RecomCourse from './RecomCourse';

class RecomCourseArea extends React.Component {
  getCourses() {
    let completed = Object.values(this.props.compleCourses);
    let courses = [];
    let interests = [];
    // generate the list of interests
    for (const course of Object.values(this.props.courses)) {
      // generate the list of interests
      if (this.props.interestedCourses.indexOf(course.number) !== -1)
        for (const keyword of course.keywords)
          if (interests.indexOf(keyword) === -1)
            interests.push(keyword);
    }

    // get courses based on interests
    for (const course of Object.values(this.props.courses)) {
      // courses with interested keyword but not completed
      if (completed && completed[0].includes(course.number)) {
        continue;
      }
      var cur_interest = "";
      for (const keyword of course.keywords)
          if (interests.indexOf(keyword) !== -1)
            cur_interest += keyword + ", "

      // if some keyword is interested
      if (cur_interest !== ""){
        courses.push(
          <RecomCourse key={"recom " + course.name} data={course} interests={cur_interest.substring(0, cur_interest.length - 2)} ratingCallback={this.props.rating}/>
        )
      }

    }

    return courses;
  }

  render() {
    return (
      <React.Fragment>
        <h3 style={{display: this.props.interestedCourses.length ? "none":"block"}}> Give high ratings to the completed courses that you are interested in to find recommendations!</h3>

        <div class="h-100 mt-2 ml-1">
          {this.getCourses()}
        </div>
      </React.Fragment>
    );
  }
}

export default RecomCourseArea;
